mapper.select:
SELECT A.DEVICECODE, A.LOTNO, A.PUMBUN, A.PUMCODE, B.PUMNAME, B.GIJONG, A.LOADCNT, SUBSTRING(A.STARTTIME, 6, 11) STARTTIME, SUBSTRING(A.STARTCARB, 6,11) STARTCARB, SUBSTRING(A.ENDSALT,6,11) ENDSALT, SUBSTRING(A.ENDTIME, 6, 11) ENDTIME, A.MESLOT, A.REMARK FROM t_siljuk A, t_product B WHERE 1=1 AND SUBSTRING(A.STARTTIME, 1, 16) >= #{searchStartDate} AND SUBSTRING(A.STARTTIME, 1, 16) <= DATEADD(DAY,1,#{searchEndDate})
AND A.DEVICECODE = #{devicecode}
AND A.PUMCODE = B.DOBUN ORDER BY A.DEVICECODE, A.LOTNO

mapper.delete:
DELETE FROM T_WAITLIST WHERE LOTNO = #{lotno}
DELETE FROM T_SILJUK WHERE LOTNO = #{lotno}
DELETE FROM T_WORKINLINE WHERE LOTNO = #{lotno}

mapper.update:
UPDATE T_SILJUK
SET ENDSALT = CONVERT(CHAR(16), GETDATE(), 120)
WHERE LOTNO = #{lotno}

UPDATE T_WAITLIST SET PLC = '2'
WHERE LOTNO = #{lotno}

UPDATE T_SILJUK
SET ENDTIME = CONVERT(CHAR(16), GETDATE(), 120)
WHERE LOTNO = #{lotno}

DELETE FROM T_WORKINLINE
WHERE LOTNO = #{lotno}

UPDATE T_WAITLIST
SET PLC = '2'
WHERE LOTNO = #{lotno}

UPDATE T_SILJUK
SET ENDTIME = CONVERT(CHAR(16), GETDATE(), 120)
WHERE LOTNO = #{lotno}

DELETE FROM T_WORKINLINE
WHERE LOTNO = #{lotno}

mapper.select:
SELECT A.DEVICECODE, A.PUMCODE, B.PUMNAME, B.GIJONG, SUM(A.LOADCNT) AS CNTSUM, ISNULL(SUM(CASE WHEN CUR_LOCATION < 12 THEN A.LOADCNT END), 0) AS INTRAY, ISNULL(SUM(CASE WHEN CUR_LOCATION = 12 THEN A.LOADCNT END), 0) AS OUTTRAY FROM t_siljuk A, t_product B WHERE 1=1 AND SUBSTRING(A.STARTTIME, 1, 16) >= #{searchStartDate} AND SUBSTRING(A.STARTTIME, 1, 16) <= DATEADD(DAY, 1, #{searchEndDate})
AND A.DEVICECODE = #{devicecode}
AND A.PUMCODE = B.dobun GROUP BY A.DEVICECODE, A.PUMCODE, B.PUMNAME, B.GIJONG

SELECT keymonth, devicecode, pumcode, pumname, gijong, dobun, totalout, realout
  FROM t_workmonth
  WHERE 1=1

      AND keymonth = #{keymonth}


      AND devicecode = #{devicecode}

  ORDER BY keymonth DESC

SELECT keyyear, devicecode, pumcode, pumname, gijong, 
       m01, m02, m03, m04, m05, m06, 
       m07, m08, m09, m10, m11, m12
FROM HTSERVERDB_4_14.dbo.t_workyear
WHERE keyyear = #{keyyear}

    AND devicecode = #{devicecode}
